mapper.select:
select ASS_BNO,STU_CD,LEC_CD,SUBMIT_DATE,SUB_CONTENT,EVAL_CONTENT,SCORE,ANO_CD,SUB_EVAL_STATUS_CD
from ass_submit
where ass_bno = #{assBno}
and stu_cd = #{stuCd}
and lec_cd = #{lecCd}

mapper.update:
update ass_submit
set score=#{score},
eval_content = #{evalContent},
sub_eval_status_cd = 'evalcom'
where ass_bno = #{assBno}
and stu_cd = #{stuCd}
and lec_Cd = #{lecCd}
