mapper.update:
update exam_apply
set score = #{midScore}
where stu_cd = #{stuCd}
and lec_cd = #{lecCd}
and exam_seq = (select exam_seq from exam where exam_div_Cd = 'mid' and lec_cd = #{lecCd})

mapper.select:
select  STU_CD,LEC_CD,SCORE as fin_score,EXAM_SEQ from exam_apply 
where stu_cd = #{stuCd}
and lec_Cd = #{lecCd}
and exam_seq = (select distinct(exam_seq) from exam where exam_div_Cd = 'fin' and lec_cd = #{lecCd} )

mapper.update:
update exam_apply
set score = #{finScore}
where stu_cd = #{stuCd}
and lec_cd = #{lecCd}
and exam_seq = (select exam_seq from exam where exam_div_Cd = 'fin' and lec_cd = #{lecCd})

update lec
set mid_score = #{midScore}, 
	fin_score = #{finScore},
	final_marks = #{finalMarks},
	final_score = #{finalScore}
where stu_Cd = #{stuCd}
and lec_Cd = #{lecCd}
