mapper.select:
select *
from portlet
where mem_id = #{memId}
and id = #{id}
and po_avail = 'selected'

mapper.update:
update portlet
set po_avail = #{poAvail},x = #{x}, y = #{y}
where mem_id = #{memId}
and id = #{id}

update portlet
set po_avail = #{poAvail}
where mem_id = #{memId}
and id = #{id}

update portlet
set x = #{x}, y = #{y}
where mem_id = #{memId}
and id = #{id}
