mapper.select:
select distinct grade_seme_cd from lec
where stu_cd=#{stuCd}
order by grade_seme_cd

select STU_CD,(select subj_name from lec_manage lm,syllabus sy
			    where lm.subj_cd=sy.subj_cd
                  and sy.syl_cd=#{lecCd}) as subj_name,
       LEC_CD, MYLEC_STATUS_CD, GRADE_SEME_CD, EVAL_SUBJ_CONTENT, CDATE, EVAL_STATUS_CD, FINAL_SCORE, FINAL_MARKS, ATTEND_SCORE, ASS_SCORE, MID_SCORE, FIN_SCORE from lec
where lec_cd=#{lecCd}
and stu_cd=#{stuCd}

select lec_cd from lec
where stu_cd=#{stuCd}
and grade_seme_cd=#{gradeSemeCd}
order by lec_cd

select attend_status_cd,count(attend_status_cd) as count
from attend
where lec_cd=#{lecCd}
and stu_cd=#{stuCd}
group by attend_status_cd
order by attend_status_cd


select grade||'-'||stu_semester as grade_seme,total_score,seme_cd from semester_stu
where stu_cd = #{stuCd}
order by seme_cd
