mapper.select:

 select attend_status_cd , count(attend_status_cd) attend_cnt from attend
where stu_cd=#{stuCd}
and lec_cd=#{lecCd}
group by attend_status_cd
order by attend_status_cd

mapper.update:
		  update lec
		     set MYLEC_STATUS_CD='w'
		   where stu_cd=#{stuCd}
             and lec_cd = #{lecCd}
